import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginshow: boolean = false
  emailonlyshow: boolean = true

  toggleLoginFace() {
    this.loginshow = true;
    this.emailonlyshow = false;
  }
}
