import { Component } from '@angular/core';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  loginModalOpen: boolean = false
  registerModalOpen: boolean = false


  loginModaltoggle(open: boolean) {
    this.loginModalOpen = open
  }
  registerModaltoggle(open: boolean) {
    this.registerModalOpen = open
  }
}
<button (click)="loginModaltoggle(true)" >Login</button>
<button (click)="registerModaltoggle(true)" >Register</button>
<app-modal [isOpen]="loginModalOpen" (isClose)="loginModaltoggle(false)">
  <app-login></app-login>
</app-modal>
<app-modal [isOpen]="registerModalOpen" (isClose)="registerModaltoggle(false)">
  <app-register></app-register>
</app-modal>
